import "./ExpensesFilter.css";

function ExpensesFilter() {
    function filterHandler(event) {
        console.log(event.target.value);
    }
  return (
    <div className="expenses-filter">
      <div className="expenses-filter__control">
        <label>Filter by year</label>
        <select>
          <option value="2023" onClick={filterHandler}>2023</option>
          <option value="2022" onClick={filterHandler}>2022</option>
          <option value="2021" onClick={filterHandler}>2021</option>
          <option value="2020" onClick={filterHandler}>2020</option>
        </select>
      </div>
    </div>
  );
}

export default ExpensesFilter;
